const div_toggle_click=t=>{if("CODE"!==t.target.tagName){const g=t.target,e=g.toggle_status;g.className=!1===e?"div-toggle show":"div-toggle",g.toggle_status=!0!==e}},$div_toggle=document.getElementsByClassName("div-toggle"),toggle_len=$div_toggle.length;let i=0;for(i=0;i<toggle_len;i++)$div_toggle[i].toggle_status=!1,$div_toggle[i].addEventListener("click",div_toggle_click,!1);